const SEND_POST = 'SEND_POST';
const UPDATE_POST_TEXT = 'UPDATE_POST_TEXT';
const TOGGLE_IS_FETCHING = 'TOGGLE_IS_FETCHING';
const SET_POSTS = 'SET_POSTS';
const SET_USER_PROFILE = 'SET_USER_PROFILE';

let initialState = {
    posts: [],
    currentTextPost: '',
    profile: null
}

const postsReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_POST:
            return {
                ...state,
                posts: [...state.posts, state.currentTextPost],
                currentTextPost: ''
            }

        case UPDATE_POST_TEXT:
            return {
                ...state,
                currentTextPost: action.newText
            }
        case SET_POSTS:
            return { ...state, users: action.users }
        case TOGGLE_IS_FETCHING:
                return {...state, isFetching: action.isFetching}
        case SET_USER_PROFILE:
            return {...state, profile: action.profile}        
        default: return state;

    }

}

export const sendPost = () => ({ type: SEND_POST });
export const updatePost = (newText) => ({ type: UPDATE_POST_TEXT, newText });
export const setPosts = (posts) => ({ type: UPDATE_POST_TEXT, posts });
export const setUserProfile = (profile) => ({type: SET_USER_PROFILE, profile});

export default postsReducer;
