import React from 'react';
import Profile from './Profile'
import { connect } from 'react-redux';
import { setUserProfile, sendPost, updatePost, getProfileThunk } from './../../redux/profile-reducer';
import { withRouter } from 'react-router-dom';
import withAuthRedirect from '../../hoc/withAuthRedirect';
import { compose } from 'redux';

class ProfileContainer extends React.Component {
    componentDidMount() {
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = 2;
        }

        this.props.getProfileThunk(userId);
    }
    render() {
        return <Profile {...this.props} />
    }

}
let WithUrlDataContainerComponent = withRouter(ProfileContainer);
const mapStateToProps = (state) => ({
    ProfilePage: state.ProfilePage
})
export default withAuthRedirect(connect(mapStateToProps, { setUserProfile, sendPost, updatePost, getProfileThunk })(WithUrlDataContainerComponent));

