import React from 'react';
import Profile from './Profile'
import { connect } from 'react-redux';
import { setUserProfile, sendPost, savePhotoThunk, getProfileThunk, getStatusThunk, updateStatusThunk } from './../../redux/profile-reducer';
import { withRouter } from 'react-router-dom';
import withAuthRedirect from '../../hoc/withAuthRedirect';
import { compose } from 'redux';

class ProfileContainer extends React.Component {
    debugger;
     refreshProfile(){
         
        let userId = this.props.match.params.userId;
        this.props.getProfileThunk(userId);
        this.props.getStatusThunk(userId);
    }
    componentDidMount(){
        this.refreshProfile()
    }
    componentDidUpdate(prevProps, prevState){
        if(prevProps.match.params.userId!== this.props.match.params.userId){
            this.refreshProfile()
        }
    }
    render() {
        
        return <Profile {...this.props} isMyPage={this.props.authId === this.props.match.params.userId}   />
    }

}

const mapStateToProps = (state) => ({
    ProfilePage: state.ProfilePage,
    profile: state.ProfilePage.profile,
    status: state.ProfilePage.status,
    posts: state.ProfilePage.posts,
    isAuth: state.auth.isAuth,
    authId: state.auth.id
})


 export default compose(
    connect(mapStateToProps, { setUserProfile, sendPost,  getProfileThunk, getStatusThunk, updateStatusThunk, savePhotoThunk }),
    withRouter,
    withAuthRedirect
)(ProfileContainer);
