import React from 'react';
import styles  from './DialogWithUser.module.css'


const DialogWithUser = (props) => {


    return <div onClick={()=>{props.setDialogID(props.id)}} className={styles.items + ' ' + (props.currentDialogId === props.id ? styles.selectedDialog : '')}>
            <div>
                <img src={props.avatar} alt="avatar"/>
            </div>
            <div className={styles.name}>
                {props.name}
            </div>
            <span className={styles.message}>{props.lastMessage.length >  30 ? props.lastMessage.slice(0,30) + '...'  : props.lastMessage }</span>
    </div>
}

export default DialogWithUser;
