import React from 'react';

import styles from './profileInfo.module.css';


class ProfileStatus extends React.Component {
    state = {
        editMode: false,
        status: this.props.status
    }
    editMode = () => {
        this.setState({
            editMode: true
        })
    }
    outEditMode = () => {
        this.setState({
            editMode: false
        })
        this.props.updateStatus(this.state.status)
    }
    onChangeStatus = (e) => {
        let newText = e.currentTarget.value;
        this.setState({
            status: newText
        })
    }
    componentDidUpdate(prevProps, prevState){
        if(prevState.status !== this.props.status){
            this.setState({
                status: this.props.status
            })
        }
    }

    render() {
        return <div>
            {!this.state.editMode
                ? <div>
                    <span onDoubleClick={this.editMode}>{this.props.status}</span>
                </div>
                : <div>
                    <input autoFocus={true} onChange={this.onChangeStatus}   onBlur={this.outEditMode} value={this.state.status}></input>
                </div>

            }

        </div>
    }
}

export default ProfileStatus;
