const SEND_MESSAGE = 'messages-reducer/SEND-MESSAGE';
const SET_DIALOG_ID = 'messages-reducer/SET_DIALOG_ID';

let initialState = {
    dialogItemsData: [
        {
            name: 'Suarez', id: 104, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/5081/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]

        },
        {
            name: 'Siarhei', id: 2, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/5217/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true }
            ]
        },
        {
            name: 'SeregaLucky', id: 3, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/1670/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]
        },
        {
            name: 'Andrej', id: 4, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/4987/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]
        }
    ],
    currentDialogId: 0
}

const messagesReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_MESSAGE:
            return {
                ...state,
                dialogItemsData: state.dialogItemsData.map((i) => { return i.id === action.id 
                ? { ...i, messages: [...i.messages, {message: action.message, id: i.messages.length+1, right: true}] } 
                : { ...i } })
            }
        case SET_DIALOG_ID:
            return {
                ...state,
                currentDialogId: action.id
            }

        default: return state;
    }

}

export const sendMessage = (data, id) => ({ type: SEND_MESSAGE, message: data.message, id });
export const setDialogID = (id) => ({ type: SET_DIALOG_ID, id });

export default messagesReducer;
