import React from 'react'

import {sendMessageActionCreater,updateTextActionCreater} from '../../redux/messages-reducer'
import Messages from './Messages/Messages.jsx'


const MessagesContainer = (props) => {
    let state = props.store.getState().Messages;
    
    let sendMessage = () => {
        props.dispatch(sendMessageActionCreater());
    }
    let onChangeMessage = (e) => {
        
        let text = e.target.value;
        props.dispatch(updateTextActionCreater(text));
    }

    
    return <Messages state={state} dispatch={props.store.dispatch} sendMessage={sendMessage} onChangeMessage={onChangeMessage}/>
}

export default MessagesContainer;
