import React from 'react';
import { connect } from 'react-redux';
import { followAC, unfollow, setUsers, setCurrentPage, setTotalUsersCountAC, toggleIsFetchingAC } from '../../redux/friends-reducer';
import axios from 'axios';
import FriendsFunc from './Friends';
import Preloader from '../common/Preloader/Preloader';





class FirendsContainer extends React.Component {

    componentDidMount() {
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(response.data.items);
            this.props.setTotalUsersCount(response.data.totalCount);
        })
    }
    onPageChanged = (pageNumber) => {
        this.props.toggleIsFetching(true);
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(response.data.items)
        })
    }
    render() {
        return <>
        {this.props.isFetching ? <Preloader/> : null}

         <FriendsFunc totalUsersCout={this.props.totalUsersCout}
            pageSize={this.props.pageSize}
            onPageChanged={this.onPageChanged}
            currentPage={this.props.currentPage}
            users={this.props.users}
            unfollow={this.props.unfollow}
            follow={this.props.follow}
        />
        </>
    }
}










let mapStateToProps = (state) => {
    return {
        users: state.FriendsPage.users,
        pageSize: state.FriendsPage.pageSize,
        totalUsersCout: state.FriendsPage.totalUsersCout,
        currentPage: state.FriendsPage.currentPage,
        isFetching: state.FriendsPage.isFetching
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (userId) => dispatch(followAC(userId)),
        unfollow: (userId) => dispatch(unfollowAC(userId)),
        setUsers: (users) => dispatch(setUsersAC(users)),
        setCurrentPage: (currentPage) => dispatch(setCurrentPageAC(currentPage)),
        setTotalUsersCount: (count) => dispatch(setTotalUsersCountAC(count)),
        toggleIsFetching: (isFetching) => dispatch(toggleIsFetchingAC(isFetching))
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(FirendsContainer);
