import React from 'react'
import withAuthRedirect from '../../hoc/withAuthRedirect'
import {sendMessageActionCreater,updateTextActionCreater} from '../../redux/messages-reducer'
import Messages from './Messages/Messages.jsx'
import {connect} from 'react-redux';


const mapStateToProps = (state) => {
    return {Messages: state.Messages }
}
const mapDispatchToProps = (dispatch) => {
    return {
        sendMessage: () => {
            dispatch(sendMessageActionCreater());
        },
        onChangeMessage: (text) => dispatch(updateTextActionCreater(text))
    }
}

 const MessagesContainer = withAuthRedirect(connect(mapStateToProps,mapDispatchToProps)(Messages));

export default MessagesContainer;
