let store = {
    _state: {
        Messages: {
            dialogItemsData : [
                {name: 'Henry Dinn' , id: 1 },
                {name: 'Michael Ross' , id: 2 },
                {name: 'Dick Irvin' , id: 3 },
                {name: 'Dafna Grass' , id: 4 }
            ],
            messagesData: [
                {message: 'Hey,how are you?', id: 1 },
                {message: 'Great! How are you?', id: 2, right: true},
                {message: 'Im fine! Go work!', id: 3},
                {message: 'Lets go!!!', id: 4, right: true},
            ],
            currentText: ''
        }
    }
}
let renderEntireTree = () => {
    console.log('state changed');
}



export let updateTextMessage = (newText) =>{
    state.Messages.currentText = newText;
    renderEntireTree(state);
}
export let sendMessage = () => {
    let newMessage = {
        id: 5,
        message: state.Messages.currentText,
        right: true
    }
    state.Messages.messagesData.push(newMessage);
    state.Messages.currentText = '';
    renderEntireTree(state);
}
export const subscribe = (observer) => {
    renderEntireTree = observer;
}

export default state;
