import React, { useState, useEffect } from 'react';

import styles from './profileStatus.module.css';


const ProfileStatusWithHooks = ({ isMyPage, ...props }) => {
    const [editMode, setEditMode] = useState(false);
    const [status, setStatus] = useState(props.status);

    useEffect(() => {
        setStatus(props.status);
    }, [props.status])

    const outEditMode = () => {
        setEditMode(false);
        setStatus(props.status);
    }
    const saveStatus = () => {
        props.updateStatus(status);
    }


    return <div>
        {isMyPage ?
                <div className={styles.statusContainer}>
                    <div>
                        <span onClick={() => { setEditMode(true) }} className={styles.status}>{props.status || 'set a status message'}</span>
                    </div>
                    {editMode && <div className={styles.changeStatusContainer}> 
                        <div><input className={styles.inputStatus} autoFocus={true} onBlur={outEditMode} onChange={(e) => { setStatus(e.currentTarget.value) }} value={status}/></div>
                        <button className={styles.saveButton} onClick={saveStatus}>Save</button>
                    </div>} 
                </div>
            : <div>
                {
                   props.status 
                   ? props.status.length > 100 ? props.status.slice(0,100) : props.status
                   : null
               }
            </div>
        }

    </div>

}

export default ProfileStatusWithHooks;
