import React from 'react';
import Profile from './Profile'
import { connect } from 'react-redux';
import { resetProfile, setUserProfile, sendPost, savePhotoThunk, getProfileThunk, getStatusThunk, updateStatusThunk, saveProfileData, setEditProfile,
 getFollowedThunk, followUnfollow, setMessageMode, setPopupAvatar, likesToggle, deletePost} from './../../redux/profile-reducer';
import { withRouter } from 'react-router-dom';
import withAuthRedirect from '../../hoc/withAuthRedirect';
import { compose } from 'redux';
import { profileStatus, profilePosts, getIsAuth, getAuthId, getIsEditMode, getProfile, getFollowed, getIsFetching, getIsSendMode, getIsAvatarPopup } from './profile-selectors';
import Preloader from '../common/Preloader/Preloader';

class ProfileContainer extends React.Component {
   
     refreshProfile(){
         
        let userId = this.props.match.params.userId;
        this.props.getProfileThunk(userId);
        this.props.getStatusThunk(userId);
        this.props.getFollowedThunk(userId);
    }
    componentWillUnmount(){
        this.props.resetProfile();
    }
    componentDidMount(){
        console.log('componentDidMount');
        this.refreshProfile()
        
    }
    componentDidUpdate(prevProps, prevState){
        if(prevProps.match.params.userId !== this.props.match.params.userId){
            this.props.resetProfile();
            this.refreshProfile();
        }
    }
    render() {
        debugger;
        console.log('render');
        return <>
                {this.props.isFetching && < Preloader/>}
                <Profile {...this.props} userId={this.props.match.params.userId}  isMyPage={this.props.authId == this.props.match.params.userId}   />
            </>
    }   

}

const mapStateToProps = (state) => ({
    profile: getProfile(state),
    status: profileStatus(state),
    posts: profilePosts(state),
    isAuth: getIsAuth(state),
    authId: getAuthId(state),
    isEditMode: getIsEditMode(state),
    isFollowed: getFollowed(state),
    isFetching: getIsFetching(state),
    sendMessageMode: getIsSendMode(state),
    avatarPopup: getIsAvatarPopup(state)
})


 export default compose(
    connect(mapStateToProps, { setUserProfile, sendPost,  getProfileThunk, getStatusThunk, 
        updateStatusThunk, savePhotoThunk, saveProfileData, setEditProfile, resetProfile, getFollowedThunk, followUnfollow, setMessageMode, setPopupAvatar, 
        likesToggle, deletePost }),
    withRouter,
    withAuthRedirect
)(ProfileContainer);
