import React from 'react';
import styles from './posts.module.css'


const Posts = (props) => {
    let updatePost = (e) =>{    
        let text = e.target.value;
        props.updatePost(text);
    }
    let sendPost = () =>{
        props.sendPost();
    }
    return <div>
        <textarea name="" id="" cols="30" rows="10" onChange={updatePost} value={props.currentTextPost}></textarea>
        <button onClick={sendPost}>Post</button>
        {props.posts.map(p => <div>{p.text}</div>)}
    </div>
}

export default Posts;
