import React from 'react';
import { connect } from 'react-redux';
import { follow, unfollow, setUsers, setCurrentPage, setTotalUsersCount, toggleIsFetching, toggleFollowingProgress, getUsersThunk, changeCurrentPageThunk} from '../../redux/friends-reducer';
import FriendsFunc from './Friends';
import Preloader from '../common/Preloader/Preloader';
import {usersAPI} from '../../API/api';

 


class FirendsContainer extends React.Component {

    componentDidMount() {
        this.props.getUsersThunk(this.props.currentPage,this.props.pageSize)
    }
    onPageChanged = (pageNumber) => {
        this.props.toggleIsFetching(true);
        this.props.setCurrentPage(pageNumber);
        usersAPI.getUsers(pageNumber,this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(data.items)
        })
    }
    render() {
        return <>
            {this.props.isFetching ? <Preloader /> : null}

            <FriendsFunc {...this.props}
            />
        </>
    }
}


let mapStateToProps = (state) => {
    return {
        users: state.FriendsPage.users,
        pageSize: state.FriendsPage.pageSize,
        totalUsersCout: state.FriendsPage.totalUsersCout,
        currentPage: state.FriendsPage.currentPage,
        isFetching: state.FriendsPage.isFetching,
        followingInProgress: state.FriendsPage.followingInProgress
    }
}


export default connect(mapStateToProps, {
    follow, unfollow, setUsers, setCurrentPage, setTotalUsersCount, toggleIsFetching, toggleFollowingProgress, getUsersThunk, 
})(FirendsContainer);
