import React, { useEffect, useRef} from 'react'

import MessageItem from './MessageItem/MessageItem'
import styles from './Messages.module.css'

import ScrollbarHOC from '../../common/HOCScrollBar/HOCScrollbarCusctom';
import { NavLink } from 'react-router-dom';





import AddMessageFormWithConnect from './AddMessageFormWithConnect'


const DialogSession = ({dialogItemsData, ...props}) => {
    

    const scrollBar = useRef()

    useEffect(() => {
        !props.isSearchMode && document.querySelector(`textarea[name=message${props.userId}]`).focus()
    })

    useEffect(() => {
        scrollBar.current.scrollToBottom();
    }, [messageItems])
    const currentDialog = dialogItemsData[dialogItemsData.findIndex((i) => { return i.userId === props.currentDialogId })];
    
    const messageItems = props.messages.map((item, index) => <MessageItem time={item.timeFormat} key={index} right={item.right} message={item.message} id={item.id} />)

    return currentDialog === -1 
    ? <div className={styles.emptyMessages}>
                <div className={styles.iconMessage}></div>
                <div className={styles.selectDialog}>
                    Please select a dialog
                </div>
    </div>
    :<div className={styles.dialog}>
        <div className={styles.dialogHead}>
            <div className={styles.avatarContainer}>
                <NavLink to={`/profile/${props.userId}`}>
                    <img className={styles.avatar} src={props.avatar} alt="avatar" />
                </NavLink>
            </div>
            <span className={styles.name}>
                <NavLink to={`/profile/${props.userId}`}>
                    {props.name}
                </NavLink>
            </span>
            {props.isMobile && <span>Back to dialogs</span>}
        </div>
        <ScrollbarHOC children={<div className={styles.messagesFill}>{messageItems}</div>}  ref={scrollBar} removeTrackYWhenNotUsed={true} style={{ width: "100%", height: "75%" }} />

        <div className={styles.formContainer}>
            <AddMessageFormWithConnect userId={props.userId} name={props.name} onSubmit={(data) => { props.sendMessage(data[`message${props.userId}`], props.userId) }} />
        </div>
    </div>
}





export default DialogSession;
