import React from 'react';
import './App.css'
import SideBar from './components/SideBar/SideBar'
import { BrowserRouter, Route } from 'react-router-dom'
import ProfileContainer from './components/Profile/ProfileContainer'
import MessagesContainer from './components/Messages-container/MessagesContainer'
import News from './components/News/News'
import Communities from './components/Communities/Communities'
import Events from './components/Events/Events'
import Pages from './components/Pages/Pages'
import Manage from './components/Manage/Manage'
import FriendsContainer from './components/Friends/FriendsAPIComponent'
import FooterContainer from './components/Footer/FooterContainer';
import Login from './components/Login/Login'
import { compose } from 'redux';
import { connect } from 'react-redux';
import { initializeAppThunk } from './redux/app-reducer';
import { withRouter } from 'react-router-dom';
import Preloader from './components/common/Preloader/Preloader';

function requireAll(r) { r.keys().forEach(r); } //import files
requireAll(require.context('./img/', true));


class App extends React.Component {

  componentDidMount() {
    this.props.initializeAppThunk()
  }
  render() {
    if (!this.props.initialized) return <Preloader />

    return (
      <BrowserRouter>
        <div className='app-wrapper'>
          <SideBar />
          <div className='app-wrapper-content'>
            <Route path='/profile/:userId?' render={() => <ProfileContainer />} />
            <Route path='/news' component={News} />
            <Route exact path='/messages' render={() => <MessagesContainer />} />
            <Route path='/friends' render={() => <FriendsContainer />} />
            <Route path='/communities' component={Communities} />
            <Route path='/events' component={Events} />
            <Route path='/pages' component={Pages} />
            <Route path='/manage' component={Manage} />
            <Route path='/login' component={Login} />
          </div>
        </div>
        <FooterContainer />
      </BrowserRouter>
    );
  }
}

const mapStateToPropse = (state) => (
  {
    initialized: state.app.initialized
  }
)

export default compose(
  connect(mapStateToPropse, initializeAppThunk)
)(App);
