import React, { useRef } from 'react';
import styles from './posts.module.css'
import { reduxForm, Field } from 'redux-form';
import { required, maxLengthCreator } from '../../../utils/validators/validator';
import { Textarea } from '../../common/FormControls/FormControls';
import avatar from '../../../assets/img/ava.jpg';
import Preloader from '../../common/Preloader/Preloader';

const maxLength500 = maxLengthCreator(500);

const FormPost = (props) => {
    return <form className={styles.formPost} onSubmit={props.handleSubmit}>
        <Field placeholder="Share your great thoughts here" component={Textarea} name='postBody' validate={[required, maxLength500]} />

        <button className={styles.button} type='submit' >Post</button>
    </form>
}
const FormPostRedux = reduxForm({ form: 'formPost' })(FormPost);

const Posts = (props) => {
    
    const newOption = useRef(null);
    const popularOption = useRef(null);
    const sortPost = (e) => {
        debugger;
        e.currentTarget === newOption ? props.sortByTime() : props.sortByLikes()
    }

    return props.profile
        ? <div className={styles.postsContainer}>
            <FormPostRedux onSubmit={props.sendPost} />
            {props.posts.length ?
                <>Sort by <select onChange={sortPost}>
                    <option ref={newOption}>New</option>
                    <option ref={popularOption}>Popular</option>
                </select></> : null}
            {props.posts.map((p) => <div>
                <div className={styles.posts} key={p.id} className={styles.itemPost}>
                    <div className={styles.headerPost}>
                        <img className={styles.avatar} src={props.profile.photos.small || avatar} alt="" />

                        <div className={styles.nameBlock}>
                            <div className={styles.name}>{props.profile.fullName}</div>
                            <div >{p.timeFormat}</div>
                        </div>
                    </div>
                    <div className={styles.postBody}>
                        {p.text}
                    </div>
                    <div className={styles.menu}><span className={styles.hidenMenu} onClick={() => { props.deletePost(p.id) }} >Delete Post</span></div>
                    <div className={styles.likesContainer}>
                        <div onClick={() => { p.liked ? props.likesToggle(false, p.id) : props.likesToggle(true, p.id) }} className={styles.likes + ' ' + (p.liked && styles.liked)}></div>
                        <span className={styles.likesCount}>{p.likesCount > 0 && p.likesCount}</span>  </div>
                </div>

            </div>)}
        </div>
        : <Preloader />
}

export default Posts;
