const SEND_MESSAGE = 'SEND-MESSAGE';
const UPDATE_TEXT_MESSAGE = 'UPDATE-TEXT-MESSAGE';

let initialState = {
    dialogItemsData : [
        {name: 'Henry Dinn' , id: 1 },
        {name: 'Michael Ross' , id: 2 },
        {name: 'Dick Irvin' , id: 3 },
        {name: 'Dafna Grass' , id: 4 }
    ],
    messagesData: [
        {message: 'Hey,how are you?', id: 1 },
        {message: 'Great! How are you?', id: 2, right: true},
        {message: 'Im fine! Go work!', id: 3},
        {message: 'Lets go!!!', id: 4, right: true},
    ],
    currentText: ''
}
 
const messagesReducer = (state = initialState, action) => {
   
    switch(action.type) {
        case SEND_MESSAGE: 
            return {
                ...state,
                messagesData: [...state.messagesData, { id: 5, message: state.currentText, right: true}],
                currentText: ''
            }
        case UPDATE_TEXT_MESSAGE:
            return{
                ...state,
                currentText: action.newText
            }
        default: return state;    
            
    }

}

export const sendMessageActionCreater = () => ({type: SEND_MESSAGE }); 
export const updateTextActionCreater = (text) => ({type: UPDATE_TEXT_MESSAGE, newText: text}); 

export default messagesReducer;
