import React from 'react';
import styles from './posts.module.css'
import { reduxForm, Field } from 'redux-form';

const FormPost = (props) => {
    return <form formSubmit={props.handleSubmit}>
        <Field component='textarea' name='postBody' cols="30" rows="10" />
      
        <button className={styles.buttonPost}>Post</button>
    </form>
}
const FormPostRedux = reduxForm(FormPost, Field);

const Posts = (props) => {
    let updatePost = (e) => {
        let text = e.target.value;
        props.updatePost(text);
    }
    let sendPost = () => {
        props.sendPost();
    }
    return <div className={styles.postsContainer}>
        <FormPostRedux/>
        {props.posts.map((p) => <div key={p.id} className={styles.itemPost}>
            {p.text}
            <span className={styles.likes}>likes: {p.likesCount}</span>
        </div>)}
    </div>
}

export default Posts;
