        {props.isAuth
        ? <div>{props.login} <img className={styles.avatar} src={props.avatar}/> {<button onClick={props.logOutThunk}>logOut</button>}</div> 
        : <div>you are not authorized</div>
        }
    </div>
}


export default Footer;
