import React from 'react';

import styles from './profileInfo.module.css';


class ProfileStatus extends React.Component {

    state = {
        editMode: false
    }
    editMode = () => {
        this.setState({
            editMode: true
        })
    }
    outEditMode = () => {
        this.setState({
            editMode: false
        })
    }

    render() {
        return <div>
            {!this.state.editMode
                ? <div>
                    <span onDoubleClick={this.editMode}>{this.props.status}</span>
                </div>
                : <div>
                    <input autoFocus={true}   onBlur={this.outEditMode} value={this.props.status}></input>
                </div>

            }

        </div>
    }
}

export default ProfileStatus;
