import React from 'react';
import { connect } from 'react-redux';
import { toggleFollowingProgress, getUsersThunk, changeCurrentPageThunk, unFollowThunk, followThunk} from '../../redux/friends-reducer';
import UsersFunc from './Users';
import Preloader from '../common/Preloader/Preloader';
import withAuthRedirect from '../../hoc/withAuthRedirect'
import { compose } from 'redux';
import { getUsers, getPageSize, getTotalUsersCout, getCurrentPage, getIsFetching, getFollowingInProgress, getMessageId } from '../../redux/user-selectors';

 


class UsersContainer extends React.Component {

    componentDidMount() {
        const {currentPage, pageSize} = this.props;
        this.props.getUsersThunk(currentPage, pageSize)
    }
    onPageChanged = (pageNumber) => {
        const {pageSize} = this.props;
        this.props.changeCurrentPageThunk(pageNumber, pageSize);
    }
    render() {
        return <>
            {this.props.isFetching ? <Preloader /> : null}

            <UsersFunc {...this.props} onPageChanged={this.onPageChanged}/>
        </>
    }
}


let mapStateToProps = (state) => {
    return {
        users: getUsers(state),
        pageSize: getPageSize(state),
        totalUsersCount: getTotalUsersCout(state),
        currentPage: getCurrentPage(state),
        isFetching: getIsFetching(state),
        followingInProgress: getFollowingInProgress(state),
        messageId: getMessageId(state)
    }
}




export default compose(
    connect(mapStateToProps, {
        toggleFollowingProgress, getUsersThunk, changeCurrentPageThunk, unFollowThunk, followThunk
     }),
    withAuthRedirect
)(UsersContainer)
