import React from 'react'

import {sendMessageActionCreater,updateTextActionCreater} from '../../redux/messages-reducer'
import Messages from './Messages/Messages.jsx'


const MessagesContainer = (props) => {
    let state = props.store.getState();
    
    let sendMessage = () => {
        props.store.dispatch(sendMessageActionCreater());
    }
    let onChangeMessage = (text) => {
        
        props.store.dispatch(updateTextActionCreater(text));
    }

    
    return <Messages Messages={state.Messages}  sendMessage={sendMessage} onChangeMessage={onChangeMessage}/>
}

export default MessagesContainer;
