import React from 'react';
import Profile from './Profile'
import { connect } from 'react-redux';
import { resetProfile, setUserProfile, sendPost, savePhotoThunk, getProfileThunk, getStatusThunk, updateStatusThunk, saveProfileData, setEditProfile } from './../../redux/profile-reducer';
import { withRouter } from 'react-router-dom';
import withAuthRedirect from '../../hoc/withAuthRedirect';
import { compose } from 'redux';
import { profileStatus, profilePosts, getIsAuth, getAuthId, getIsEditMode, getProfile } from './profile-selectors';

class ProfileContainer extends React.Component {
    
     refreshProfile(){
         
        let userId = this.props.match.params.userId;
        this.props.getProfileThunk(userId);
        this.props.getStatusThunk(userId);
    }
    componentWillUnmount(){
        this.resetProfile();
    }
    componentDidMount(){
        console.log('componentDidMount');
        this.props.refreshProfile()
    }
    componentDidUpdate(prevProps, prevState){
        if(prevProps.match.params.userId !== this.props.match.params.userId){
            this.refreshProfile()
        }
    }
    render() {
        console.log('render');
        return <Profile {...this.props} isMyPage={this.props.authId == this.props.match.params.userId}   />
    }

}

const mapStateToProps = (state) => ({
    profile: getProfile(state),
    status: profileStatus(state),
    posts: profilePosts(state),
    isAuth: getIsAuth(state),
    authId: getAuthId(state),
    isEditMode: getIsEditMode(state)
})


 export default compose(
    connect(mapStateToProps, { setUserProfile, sendPost,  getProfileThunk, getStatusThunk, 
        updateStatusThunk, savePhotoThunk, saveProfileData, setEditProfile, resetProfile }),
    withRouter,
    withAuthRedirect
)(ProfileContainer);
