import withAuthRedirect from '../../hoc/withAuthRedirect'
import {sendMessage, setDialogID, search} from '../../redux/messages-reducer'
import Messages from './Messages/Messages.jsx'
import {connect} from 'react-redux'
import { compose } from 'redux'


const mapStateToProps = (state) => {
    return {
        dialogItemsData: state.Messages.dialogItemsData,
        currentDialogId: state.Messages.currentDialogId,
        dialogsFromSearch: state.Messages.dialogsFromSearch
      
     }
}

  const MessagerContainer = compose(
    connect(mapStateToProps,{sendMessage,setDialogID, search}),
    withAuthRedirect,
    withRouter
)(Messages)


export default MessagerContainer
