import React from 'react';



const Posts = (props) => {
    let updatePost = (e) =>{
        props.updatePost(e);
    }
    return <div>
        <textarea name="" id="" cols="30" rows="10" onChange={props.updatePost(e)} value={props.currentTextPost}></textarea>
        <button onClick={props.sendPost}>Post</button>
    </div>
}

export default Posts;
