import React from 'react';
import { connect } from 'react-redux';
import FriendsAPIComponent from './FriendsAPIComponent';
import { followAC, unfollowAC, setUsersAC, setCurrentPageAC, setTotalUsersCountAC } from '../../redux/friends-reducer';

let mapStateToProps = (state) => {
    return {
        users: state.FriendsPage.users,
        pageSize: state.FriendsPage.pageSize,
        totalUsersCout: state.FriendsPage.totalUsersCout,
        currentPage: state.FriendsPage.currentPage
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (userId) => dispatch(followAC(userId)),
        unfollow: (userId) => dispatch(unfollowAC(userId)),
        setUsers: (users) => dispatch(setUsersAC(users)),
        setCurrentPage: (currentPage) => dispatch(setCurrentPageAC(currentPage)),
        setTotalUsersCount: (count) => dispatch(setTotalUsersCountAC(count))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(FriendsAPIComponent);
