import { profileAPI } from '../API/api'
import { stopSubmit } from 'redux-form'
import { usersAPI } from '../API/api'



const SEND_POST = 'profile-reducer/SEND_POST'
const SET_USER_PROFILE = 'profile-reducer/SET_USER_PROFILE'
const SET_USER_STATUS = 'profile-reducer/SET_USER_STATUS'
const SET_PHOTO_PROFILE = 'profile-reducer/SET_PHOTO_PROFILE'
const SET_EDIT_PROFILE = 'profile-reducer/SET_EDIT_PROFILE'
const RESET_USER_POFILE = 'profile-reducer/RESET_USER_POFILE'
const FOLLOW_UNFOLLOW_SUCCESS = 'profile-reducer/FOLLOW_UNFOLLOW_SUCCESS'
const TOGGLE_IS_FETCHING = 'profile-reducer/TOGGLE_IS_FETCHING'
const SEND_MESSAGE_MODE = 'profile-reducer/SEND_MESSAGE_MODE'
const SET_POPPUP_AVATAR = 'profile-reducer/SET_POPPUP_AVATAR'
const TOGGLE_LIKE_SUCCESS = 'profile-reducer/TOGGLE_LIKE_SUCCESS'
const DELETE_POST_SUCCESS = 'profile-reducer/DELETE_POST_SUCCESS'
const SORT_BY_TIME = 'profile-reducer/SORT_BY_TIME'
const SORT_BY_LIKES = 'profile-reducer/SORT_BY_LIKES'
const IS_USER_EXIST = 'profile-reducer/IS_USER_EXIST'

const options = {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    timezone: 'UTC',
    hour: 'numeric',
    minute: 'numeric'
}


const initialState = {
    posts: [
        {
            name: 'ChelCode', userId: '1601', avatar: 'https://social-network.samuraijs.com/activecontent/images/users/1601/user-small.jpg?v=13',
            text: 'Whats up??!', id: 2, likesCount: 0, time: 1574713363365, timeFormat: 'November 11, 2019, 10:20 PM', liked: false
        },
        {
            name: 'ChelCode', avatar: 'https://social-network.samuraijs.com/activecontent/images/users/1601/user-small.jpg?v=13',
            text: 'hello world!!', userId: '1601', id: 1, likesCount: 12, time: 1574713324589, timeFormat: 'November 9, 2019, 2:46 PM', liked: false
        }],
    currentTextPost: '',
    profile: null,
    status: '',
    isEditMode: false,
    isFollowed: false,
    isFetching: false,
    sendMessageMode: false,
    avatarPopup: false,
    isUserExist: true
}

const profileReducer = (state = initialState, action) => {
    switch (action.type) {

        case SEND_POST:
            return {
                ...state,
                posts: [{
                    text: action.post.postBody, time: action.time, id: state.posts.length + 1, likesCount: 0, timeFormat: action.timeFormat, liked: false,
                    name: action.name, avatar: action.avatar, userId: action.userId
                }, ...state.posts],
            }
        case SET_USER_PROFILE:
            return { ...state, profile: action.profile }
        case SET_USER_STATUS:
            return { ...state, status: action.status }
        case SET_PHOTO_PROFILE:
            return { ...state, profile: { ...state.profile, photos: action.photos } }
        case SET_EDIT_PROFILE:
            return { ...state, isEditMode: action.mode }
        case RESET_USER_POFILE:
            return initialState
        case FOLLOW_UNFOLLOW_SUCCESS:
            return { ...state, isFollowed: action.isFollow }
        case TOGGLE_IS_FETCHING:
            return { ...state, isFetching: action.isFetching }
        case SEND_MESSAGE_MODE:
            return { ...state, sendMessageMode: action.isSendMode }
        case SET_POPPUP_AVATAR:
            return { ...state, avatarPopup: action.isPopup }
        case TOGGLE_LIKE_SUCCESS:
            return { ...state, posts: state.posts.map(p => action.idPost === p.id ? { ...p, liked: action.isLiked, likesCount: action.isLiked ? p.likesCount + 1 : p.likesCount - 1 } : p) }
        case DELETE_POST_SUCCESS:
            return { ...state, posts: state.posts.filter(p => p.id !== action.idPost).map((p, index) => { return { ...p, id: index + 1 } }) }
        case SORT_BY_TIME:
            return { ...state, posts: [...state.posts.sort((a, b) => { return b.time - a.time })] }
        case SORT_BY_LIKES:
            return { ...state, posts: [...state.posts.sort((a, b) => { return b.likesCount - a.likesCount })] }
        case IS_USER_EXIST:
            return {...state, isUserExist: action.payload}    
        default: return state

    }

}




export const toggleIsFetching = (isFetching) => ({ type: TOGGLE_IS_FETCHING, isFetching })
export const resetProfile = () => ({ type: RESET_USER_POFILE })
export const sendPost = (post, name, avatar, userId) => ({ type: SEND_POST, post, timeFormat: new Date().toLocaleString("en-US", options), time: Date.now(), name, avatar, userId })


export const setEditProfile = (mode) => ({ type: SET_EDIT_PROFILE, mode })
export const setUserProfile = (profile) => ({ type: SET_USER_PROFILE, profile })
export const setUserStatus = (status) => ({ type: SET_USER_STATUS, status })
export const setFollowUnfollow = (isFollow) => ({ type: FOLLOW_UNFOLLOW_SUCCESS, isFollow })
export const setMessageMode = (isSendMode) => ({ type: SEND_MESSAGE_MODE, isSendMode })
export const setPopupAvatar = (isPopup) => ({ type: SET_POPPUP_AVATAR, isPopup })
export const likesToggle = (isLiked, idPost) => ({ type: TOGGLE_LIKE_SUCCESS, isLiked, idPost })
export const deletePost = (idPost) => ({ type: DELETE_POST_SUCCESS, idPost })
export const sortByTime = () => ({ type: SORT_BY_TIME })
export const sortByLikes = () => ({ type: SORT_BY_LIKES })
export const setIsUserExist = (isExist) => ({ type: IS_USER_EXIST, payload: isExist })

export const sendPostThunk = (post) => (dispatch, getState) => {

    const name = getState().auth.login
    const avatar = getState().auth.avatar
    const userId = getState().auth.id
    dispatch(sendPost(post, name, avatar, userId))
}
export const getProfileThunk = (userId) => async (dispatch) => {
   
    const data = await (profileAPI.getProfile(userId)).catch((e)=>{
            dispatch(setIsUserExist(false))
    }
    )
    if(data) dispatch(setUserProfile(data))
}

const savePhotoSucсess = (photos) => ({ type: SET_PHOTO_PROFILE, photos })

export const getStatusThunk = (userId) => async (dispatch) => {
    const data = await (profileAPI.getUserStatus(userId))

    dispatch(setUserStatus(data))

}
export const updateStatusThunk = (status) => async (dispatch) => {
    const data = await (profileAPI.updateUserStatus(status))

    if (data.resultCode === 0) {
        dispatch(setUserStatus(status))
    }

}
export const savePhotoThunk = (photo) => async (dispatch) => {
    const data = await (profileAPI.updatePhoto(photo))

    if (data.resultCode === 0) {
        dispatch(savePhotoSucсess(data.data.photos))
    }

}
export const saveProfileData = (profile) => async (dispatch, getState) => {
    const userId = getState().auth.id
    const response = await (profileAPI.saveProfileData(profile))


    if (response.resultCode === 0) {
        dispatch(getProfileThunk(userId))
        dispatch(setEditProfile(false))
    } else {
        let number = response.messages[0].indexOf('>')
        let test = response.messages[0].slice(number + 1, -1).toLowerCase()
        dispatch(stopSubmit('edit-profile', { 'contacts': { [test]: test + ' incorrect' } }))
    }
}
export const getFollowedThunk = (id) => async (dispatch) => {
    const data = await (usersAPI.getFollow(id))
    dispatch(setFollowUnfollow(data))

}
export const followUnfollow = (isFollow, id) => async (dispatch) => {
    dispatch(toggleIsFetching(true))
    const data =
        isFollow
            ? await usersAPI.postFollow(id)
            : await usersAPI.deleteFollow(id)
    if (data.resultCode === 0) {
        dispatch(setFollowUnfollow(isFollow))
        dispatch(toggleIsFetching(false))
    }


}
export default profileReducer
