import React, { useEffect, useRef } from 'react'

import MessageItem from '../../MessageItem/MessageItem'
import styles from './Messages.module.css'
import { reduxForm, reset, change, formValues  } from 'redux-form';
import { createField } from '../../common/FormControls/FormControls';
import { required, voidValue } from '../../../utils/validators/validator';
import ScrollbarHOC from '../../common/HOCScrollBar/HOCScrollbarCusctom';
import { NavLink } from 'react-router-dom';
import 'emoji-mart/css/emoji-mart.css'
import { NimblePicker,Picker } from 'emoji-mart'
import data from 'emoji-mart/data/messenger.json'
import {connect} from 'react-redux'

const addMessageForm = (props) => {
   
    console.log(props.addMessageForm)
    const addEmoji = (emoji)=>{
        
        debugger;
        props.dispatch(change('addMessageForm', `message${props.userId}`, props.addMessageForm.values ? props.addMessageForm.values[`message${props.userId}`] + emoji.native : emoji.native));
    }
    const onKeyHandler = (e) => {
        if (e.keyCode == 13 && e.shiftKey == false) {
            if (e.target.name == `message${props.userId}`) {
                e.preventDefault();
                props.handleSubmit();

            }
        }
    }
    return <form onKeyDown={onKeyHandler} onSubmit={props.handleSubmit}>
        {createField(`write message to ${props.name}`, `message${props.userId}`, [required], 'textarea', null, null, { autoFocus: true })}

       {props.dirty && <button className={styles.buttonForm} type="submit"></button> }
       <span role='button' className={styles.buttomEmoji}>🙂</span>
       <Picker set='emojione' onSelect={addEmoji} /> 
       <NimblePicker set='messenger' data={data} onSelect={addEmoji} />

    </form>
}

const afterSumbit = (result, dispatch) => {
    dispatch(reset('addMessageForm'))
}
const AddMessageFormRedux = reduxForm({
    form: 'addMessageForm',
    onSubmitSuccess: afterSumbit
})(addMessageForm);

const Dialog = (props) => {
    let messageItems = props.messages.map((item, index) => <MessageItem key={index} right={item.right} message={item.message} id={item.id} />)

    const scrollBar = useRef()

    useEffect(() => {
        !props.isSearchMode && document.querySelector(`textarea[name=message${props.userId}]`).focus()
    })

    useEffect(() => {
        scrollBar.current.scrollToBottom();
    }, [messageItems])
    return <div className={styles.dialog}>
        <div className={styles.dialogHead}>
            <div className={styles.avatarContainer}>
                <NavLink to={`/profile/${props.userId}`}>
                    <img className={styles.avatar} src={props.avatar} alt="avatar" />
                </NavLink>
            </div>
            <span className={styles.name}>
                <NavLink to={`/profile/${props.userId}`}>
                    {props.name}
                </NavLink>
            </span>
        </div>
        <ScrollbarHOC children={<div className={styles.messagesFill}>{messageItems}</div>} autoHide ref={scrollBar} removeTrackYWhenNotUsed={true} style={{ width: "100%", height: "75%" }} />

        <div className={styles.formContainer}>
            <AddMessageFormWithConnect valueMessage={props.valueMessage} userId={props.userId} name={props.name} onSubmit={(data) => { debugger; props.sendMessage(data[`message${props.userId}`], props.userId) }} />
        </div>
    </div>
}
const mapStateToPropse = (state)=> ({
    addMessageForm: state.form.addMessageForm
})
const AddMessageFormWithConnect = connect(mapStateToPropse)(AddMessageFormRedux)

export default Dialog;
