import React from 'react'
import styles from './Messages.module.css'
import DialogItem from '../DialogItem/DialogItem'
import MessageItem from '../MessageItem/MessageItem'


let dialogItemsData = [
    {name: 'Henry Dinn' , id: 1 },
    {name: 'Michael Ross' , id: 2 },
    {name: 'Dick Irvin' , id: 3 },
    {name: 'Dafna Grass' , id: 4 }
]
let messagesData = [
    {message: 'Hey,how are you?', id: 1},
    {message: 'Great! How are you?', id: 2},
    {message: 'Im fine! Go work!', id: 3},
    {message: 'Lets go!!!', id: 4},
]
let messageItem = messagesData.map( item => <MessageItem message={item.message} id={item.id} />)
let dialogItems = dialogItemsData.map((item) =>{
    const {id, name} = item;
    return <DialogItem name={name} id={id}/>
})
const Messages = () => {
    return <div className={styles.messages}>
        <div className={styles.dialog}>
            <div className={styles.dialogHead}></div>
            <div className={styles.dialogBody}>
                {messageItem}
            </div>
        </div>
        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}></div>
                {dialogItems}
        </div>
    </div>
}
export default Messages;
