import React from 'react'

import styles from './Messages.module.css'
import DialogWithUser from '../DialogsList/DialogWithUser'
import Dialog from './Dialog'









const Messages = ({dialogItemsData,...props}) => {
    debugger;
    let dialogItems = dialogItemsData.map((item, index) => <DialogWithUser lastMessage={ item.messages[item.messages.length-1].message} setDialogID={props.setDialogID}   key={index+1} name={item.name} id={item.id} />)


    return <div className={styles.messages}>
        {props.currentDialogId === 0
            ? 'chose dialog please'
            : <Dialog sendMessage={props.sendMessage} id={props.currentDialogId} messages={dialogItemsData[props.currentDialogId-1].messages} />}

        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}></div>
            <div className={styles.dialogsList}>
                {dialogItems}

            </div>
        </div>
        
    </div>
}


export default Messages;
