import React, {useState} from 'react';
import styles from './posts.module.css'
import { reduxForm, Field, reset, change } from 'redux-form';
import { required, maxLengthCreator, voidValue } from '../../../utils/validators/validator';
import { Textarea } from '../../common/FormControls/FormControls';
import avatar from '../../../assets/img/ava.jpg';
import Preloader from '../../common/Preloader/Preloader';
import { NavLink } from 'react-router-dom'
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'
import {EmojiPicker} from './../../common/EmojiPicker/EmojiPicker'

const maxLength500 = maxLengthCreator(500);

const FormPost = (props) => {
    const [emojiPickerMode, setEPMode] = useState(false);

    const addEmoji = (e) => {
        const sym = e.unified.split('-')
        const codesArray = []

        sym.map(el => codesArray.push('0x' + el))
        const emoji = String.fromCodePoint(...codesArray)

        props.dispatch(change('addMessageForm', `message${props.userId}`, props.addMessageForm.values ? props.addMessageForm.values[`message${props.userId}`] + emoji : emoji));
    }

    return <form className={styles.formPost} onSubmit={props.handleSubmit}>
        <Field placeholder="Share your great thoughts here" component={Textarea} name='postBody' validate={[required, maxLength500, voidValue]} />
        <EmojiPicker/>
        <button className={styles.button} type='submit' >Post</button>
    </form>
}
const afterSumbit = (result, dispatch) => {
    dispatch(reset('formPost')) 
}
const FormPostRedux = reduxForm({ 
    form: 'formPost',
    onSubmitSuccess: afterSumbit
})(FormPost);

const Posts = (props) => {
    
    
    const sortPost = (e) => {
        e.target.value === 'New' ? props.sortByTime() : props.sortByLikes()
    }

    return props.profile
        ? <div className={styles.postsContainer}>
            <FormPostRedux onSubmit={props.sendPostThunk} />
            {props.posts.length ?
                <>Sort by <select className={styles.selectSort} onChange={sortPost}>
                    <option >New</option>
                    <option >Popular</option>
                </select></> : null}
            {props.posts.map((p) => <div>
                <div className={styles.posts} key={p.id} className={styles.itemPost}>
                    <div className={styles.headerPost}>
                      <NavLink to={`/profile/${p.userId}`}> <img className={styles.avatar} src={p.avatar || avatar} alt="" /></NavLink> 

                        <div className={styles.nameBlock}>
                        <NavLink to={`/profile/${p.userId}`}> <div className={p.name}>{p.name}</div></NavLink> 
                            <div >{p.timeFormat}</div>
                        </div>
                    </div>
                    <div className={styles.postBody}>
                        {p.text}
                    </div>
                    <div className={styles.menu}><span className={styles.hidenMenu} onClick={() => { props.deletePost(p.id) }} ><div className={styles.menuItem}>Delete post</div></span></div>
                    <div className={styles.postFooter}>
                        
                        <div className={styles.likesBody}>
                            <div className={styles.likesWrapper} onClick={() => { p.liked ? props.likesToggle(false, p.id) : props.likesToggle(true, p.id) }}>
                                <div  className={styles.likes + ' ' + (p.liked && styles.liked)}></div>
                                <span className={styles.likesCount + ' ' + (p.liked&& styles.likesCountLiked)}>{p.likesCount > 0 && p.likesCount}</span>  </div>
                            </div>
                        </div>
                </div>

            </div>)}
        </div>
        : <Preloader />
}

export default Posts;
