const SEND_MESSAGE = 'messages-reducer/SEND-MESSAGE';


let initialState = {
    dialogItemsData: [
        {
            name: 'Henry Dinn', id: 1, messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]

        },
        {
            name: 'Michael Ross', id: 2,
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]
        },
        {
            name: 'Dick Irvin', id: 3,
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]
        },
        {
            name: 'Dafna Grass', id: 4,
            messages: [
                { message: 'Hey,how are you?', id: 1 },
                { message: 'Great! How are you?', id: 2, right: true },
                { message: 'Im fine! Go work!', id: 3 },
                { message: 'Lets go!!!', id: 4, right: true },
            ]
        }
    ],
    messagesData: [
        { message: 'Hey,how are you?', id: 1 },
        { message: 'Great! How are you?', id: 2, right: true },
        { message: 'Im fine! Go work!', id: 3 },
        { message: 'Lets go!!!', id: 4, right: true },
    ]
}

const messagesReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_MESSAGE:
            return {
                ...state,
                messagesData: [...state.messagesData, { id: 5, message: action.message, right: true }]
            }
        default: return state;
    }

}

export const sendMessage = (data) => ({ type: SEND_MESSAGE, message: data.message });


export default messagesReducer;
