import React from 'react'
import styles from './Messages.module.css'
import {NavLink} from 'react-router-dom'

const DialogItem = (props) => {
    let path = '/messages/' + props.id;
    return <div className={styles.items}>
        <NavLink to={path}>{props.name}</NavLink>
    </div>
}
const Message = (props) =>{
    return <div className={styles.message}>{props.message}</div>
}
let dialogItemsData = [
    {name: 'Henry Dinn' , id: 1 },
    {name: 'Michael Ross' , id: 2 },
    {name: 'Dick Irvin' , id: 3 },
    {name: 'Dafna Grass' , id: 4 }
]
let dialogItems = dialogItemsData.map((item) =>{
    const {id, name} = item;
    return <DialogItem name={name} id={id}/>
})
const Messages = () => {
    return <div className={styles.messages}>
        <div className={styles.dialog}>
            <div className={styles.dialogHead}></div>
            <div className={styles.dialogBody}>
                <Message message='Hey,how are you?'/>
                <Message className={styles.messageOut} message='Good, how are you?'/>
                <Message message='Im fine! Thank you)'/>
            </div>
        </div>
        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}></div>
            <div className={styles.dialogsList}>
                {dialogItems}
            </div>   
        </div>
    </div>
}
export default Messages;
