const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';
const SET_CURRENT_PAGE = 'SET_CURRENT_PAGE';

let initialState = {
    users: [],
    pageSize: 5,
    totalUsersCout: 20,
    currentPage: 1
}

const friendsReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: true } : u)
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: false } : u)
            }
        case SET_USERS:
            return { ...state, users: [...state.users, ...action.users] }
        default: return state;
        case SET_CURRENT_PAGE:
            return { ...state, curretPage: action.currentPage}
    }

}

export const followAC = (id) => ({ type: FOLLOW, id });
export const unfollowAC = (id) => ({ type: UNFOLLOW, id });
export const setUsersAC = (users) => ({ type: SET_USERS, users });
export const setCurrentPage = (currentPage) => ({ type: SET_USERS, currentPage });


export default friendsReducer;
