import React from 'react';
import styles from './Friends.module.css'
import avatar from '../../assets/img/ava.jpg'
import {NavLink} from 'react-router-dom'
import axios from 'axios';

let FriendsFunc = (props) => {
    let pageCount = Math.ceil(props.totalUsersCout / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pageCount; i++) {
        pages.push(i);
    }
    return <div className={styles.hz}>
        <div>
            {pages.map(p => {
                return <span className={props.currentPage === p && styles.selectedPage}
                    onClick={(e) => props.onPageChanged(p)}
                >{p}</span>

            })}
        </div>

        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                       <NavLink to={'/profile/' + u.id}>
                           <img src={u.photos.small != null ? u.photos.small : avatar} alt="" className={styles.usersAvatar} />
                        </NavLink> 
                    </div>
                    <div>
                        {u.followed
                            ? <button onClick={() => {
                                debugger;
                                axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{},{
                                    
                                    withCredentials: true,
                                    headers: {
                                        "API-KEY":"69e525b6-3f4d-44d6-93ed-36d6a3239fa0"
                                    }

                                } ).then(response => {
                                    if(response.data.resultCode === 0){
                                        props.unfollow(u.id);
                                    }
                                })
                                 
                                }}>Unfollow</button>
                            : <button onClick={() => {
                                   axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{},{
                                    withCredentials: true,
                                    headers: {
                                        "API-KEY":"69e525b6-3f4d-44d6-93ed-36d6a3239fa0"
                                    }
                                } ).then(response => {
                                    if(response.data.resultCode === 0){
                                        props.follow(u.id);
                                    }
                                    })
                            }}>Follow</button>
                        }
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.name}</div>
                    </span>
                    <span>
                        <div>u.location.country</div>
                        <div>u.location.city</div>
                    </span>
                </span>
            </div>)
        }
    </div >
}

export default FriendsFunc;
