import { authAPI, securityAPI } from '../API/api';
import { stopSubmit } from 'redux-form';


const SET_USER = 'auth-reducer/SET_USER';
const RESET_USER = 'auth-reducer/RESET_USER';
const GET_CAPTHCA_SUCCESS = 'auth-reducer/GET_CAPTHCA_SUCCESS'

let initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case SET_USER:
            return {
                ...state,
                ...action.data,
                isAuth: true
            }
        case RESET_USER:
            return {
                initialState
            }
        default: return state;

    }

}

export const setUserAuth = (data) => ({ type: SET_USER, data });
export const resetUserAuth = () => ({ type: RESET_USER });

export const getAuthThunk = () => async (dispatch) => {
    const data = await (authAPI.getAuth());
    if (data.resultCode === 0) {
        dispatch(setUserAuth(data.data));
    }

}
export const logInThunk = (dataForm) => async (dispatch) => {
    const data = await (authAPI.logIn(dataForm));

    if (data.resultCode === 0) {
        dispatch(getAuthThunk(data.data));
    } else {
        if(data.resultCode === 10){
            const captcha =  await (securityAPI.getCaptcha());
            dispatch()
        }
        let messages = data.messages.length ? data.messages[0] : 'Some error';
        dispatch(stopSubmit('login', { _error: messages }))
    }
}
export const logOutThunk = () => async (dispatch) => {
    const data = await (authAPI.logOut())
    if (data.resultCode === 0) {
        dispatch(resetUserAuth());
    }
}

export default authReducer;
