import React, {useState} from 'react';
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import { reduxForm } from 'redux-form';
import { createField, Input } from '../../common/FormControls/FormControls';
import { required } from '../../../utils/validators/validator';

const ProfileDescrition = ({profile, isMyPage, ...props}) =>{

    const [editMode, setEditMode] = useState(false);
    const onSubmit = (formData) =>{
        setEditMode(false);
        console.log(formData);
    }
    return <div>
                    
                    <h2>{profile.fullName}</h2>
                    <ProfileStatusWithHooks isMyPage={isMyPage} updateStatus={props.updateStatus} status={props.status} />
                    <div>Looking for a job: {profile.lookingForAJob ? 'yes' : 'no'}</div>
                    {profile.lookingForAJobDescription && <div>Description: {profile.lookingForAJobDescription} </div>}
                    {!editMode && Object.keys(profile.contacts).map((keys) => {
                        if( profile.contacts[keys]) {
                        return <div>{`${keys} : ${profile.contacts[keys]}`}</div>
                        }
                    })}
                   {isMyPage && !editMode && <button onClick={()=>{setEditMode(true)}}>Edit</button>}
                   {editMode && <ProfileEditorReduxForm onSubmit={onSubmit} profile={profile}/>}
                </div>
}

const ProfileEditor = ({handleSubmit, profile, ...props}) => {
    
    return <form onSubmit={handleSubmit}>
        <h3>Full name</h3>
        {createField('Full Name', 'fullName', null, Input,  false, null, profile.fullName )}
        {createField(null, 'lookingForJob',  null, Input, true, null, {type: 'checkbox'})}
        {Object.keys(profile.contacts).map( (key)=> {
           return <div>
                <h3>
                    {key} 
                 </h3>
                 {createField(key, key, null, Input,  false, null, {value: profile.contacts[key] } )}
                 </div>
        })
        }
        <button>save</button>
    </form>
}
const ProfileEditorReduxForm = reduxForm({form: 'edit-profile'})(ProfileEditor);

export default ProfileDescrition;
