import React from 'react';
import Profile from './Profile'
import { connect } from 'react-redux';
import { setUserProfile, sendPost, updatePost, getProfileThunk, getStatusThunk, updateStatusThunk } from './../../redux/profile-reducer';
import { withRouter } from 'react-router-dom';
import withAuthRedirect from '../../hoc/withAuthRedirect';
import { compose } from 'redux';

class ProfileContainer extends React.Component {
    componentDidMount() {
        let userId = this.props.match.params.userId;
        if (!userId) {
            userId = 1601;
        }

        this.props.getProfileThunk(userId);
        this.props.getStatusThunk(userId);
    }
    render() {
        return <Profile {...this.props} />
    }

}

const mapStateToProps = (state) => ({
    ProfilePage: state.ProfilePage,
    status: state.ProfilePage.status
})


 export default compose(
    connect(mapStateToProps, { setUserProfile, sendPost, updatePost, getProfileThunk, getStatusThunk, updateStatusThunk }),
    withRouter,
    withAuthRedirect
)(ProfileContainer);
