import { usersAPI } from '../API/api'


const FOLLOW = 'friends-reducer/FOLLOW';
const UNFOLLOW = 'friends-reducer/UNFOLLOW';
const SET_USERS = 'friends-reducer/SET-USERS';
const SET_CURRENT_PAGE = 'friends-reducer/SET_CURRENT_PAGE';
const SET_TOTAL_COUNT = 'friends-reducer/SET_TOTAL_COUNT';
const TOGGLE_IS_FETCHING = 'friends-reducer/TOGGLE_IS_FETCHING';
const TOGGLE_IS_FOLOWING_PROGRESS = 'friends-reducer/TOGGLE_IS_FOLOWING_PROGRESS';
const SET_MESSAGE_MODE = 'friends-reducer/SET_MESSAGE_MODE';
const ADD_MORE_USER = 'friends-reducer/ADD_MORE_USER';
const SET_FRIENDS = 'friends-reducer/SET_FRIENDS';
const TOGGLE_USER_LIST = 'friends-reducer/TOGGLE_USER_LIST';
const TOGGLE_USERS_TAB = 'friends-reducer/TOGGLE_USERS_TAB'

let initialState = {
    users: [],
    friends: [],
    pageSize: 30,
    totalUsersCout: 0,
    currentPage: 1,
    isFetching: false,
    followingInProgress: [],
    messageMode: false,
    messageId: 0,
    userList: true,
    friendsList: false
}

const friendsReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: true } : u)
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => action.id === u.id ? { ...u, followed: false } : u)
            }
        case SET_USERS:
            return { ...state, users: action.users }
        case ADD_MORE_USER:
            return { ...state, users: [...state.users,...action.users]}
        case SET_CURRENT_PAGE:
            return { ...state, currentPage: action.currentPage }
        case SET_TOTAL_COUNT:
            return { ...state, totalUsersCout: action.count }
        case TOGGLE_IS_FETCHING:
            return { ...state, isFetching: action.isFetching }
        case TOGGLE_IS_FOLOWING_PROGRESS:
            return {
                ...state,
                followingInProgress: action.isFetching
                ? [...state.followingInProgress, action.userId]
                : state.followingInProgress.filter(userId => userId !== action.userId)

            }
        case SET_MESSAGE_MODE:
            return{
                ...state,
                messageMode: action.isMessage,
                messageId: action.id
            }
        case SET_FRIENDS:
            return{
                ...state,
                friends: state.users.filter(u=> u.followed)
            }

                   
        default: return state;

    }

}

export const follow = (id) => ({ type: FOLLOW, id });
export const unfollow = (id) => ({ type: UNFOLLOW, id });
export const setUsers = (users) => ({ type: SET_USERS, users });
export const setCurrentPage = (currentPage) => ({ type: SET_CURRENT_PAGE, currentPage });
export const setTotalUsersCount = (count) => ({ type: SET_TOTAL_COUNT, count });
export const toggleIsFetching = (isFetching) => ({ type: TOGGLE_IS_FETCHING, isFetching });
export const toggleFollowingProgress = (isFetching, userId) => ({ type: TOGGLE_IS_FOLOWING_PROGRESS, isFetching, userId })
export const setMessageMode = (isMessage, id) => ({ type: SET_MESSAGE_MODE, isMessage, id})
export const setMoreUsers = (users) => ({type: ADD_MORE_USER, users})
export const setFriends = () => ({type: SET_FRIENDS})
export const toggleTabsUser = () => ({type: TOGGLE_USERS_TAB})

export const toggleUserListThunk = () => ({type: TOGGLE_USER_LIST})
export const getUsersThunk = (currentPage, pageSize) => async (dispatch) => {

    dispatch(toggleIsFetching(true));

    const data = await (usersAPI.getUsers(currentPage, pageSize));
    
    dispatch(toggleIsFetching(false));
    dispatch(setUsers(data.items));
    dispatch(setTotalUsersCount(data.totalCount));

}

export const getMoreUsers = (currentPage, pageSize) => async (dispatch) => {
    dispatch(setCurrentPage(currentPage));
    dispatch(toggleIsFetching(true));
    const data = await (usersAPI.getUsers(currentPage, pageSize));
    dispatch(setMoreUsers(data.items));
    dispatch(toggleIsFetching(false));
    dispatch(setTotalUsersCount(data.totalCount));
    
}
export const changeCurrentPageThunk = (pageNumber, pageSize) => async (dispatch) => {
    dispatch(setCurrentPage(pageNumber));
    dispatch(toggleIsFetching(true));

    const data = await (usersAPI.getUsers(pageNumber, pageSize));
    dispatch(toggleIsFetching(false));;
    dispatch(setUsers(data.items));
}

const followUnfollowFlow = async(dispatch, apiMethod, actionCreator, id) => {
    dispatch(toggleIsFetching(true));
    dispatch(toggleFollowingProgress(true, id));
    const data = await (apiMethod(id));
    if (data.resultCode === 0) {
        dispatch(actionCreator(id));
        dispatch(toggleFollowingProgress(false, id));
        dispatch(toggleIsFetching(false));
    }

}
export const unFollowThunk = (id) => async (dispatch) => {
    followUnfollowFlow(dispatch, usersAPI.deleteFollow, unfollow, id );
}
export const followThunk = (id) => async (dispatch) => {
    followUnfollowFlow(dispatch, usersAPI.postFollow, follow, id );
}

export default friendsReducer;
