import avatar from '../assets/img/ava.jpg'

const SEND_MESSAGE = 'messages-reducer/SEND-MESSAGE';
const SET_DIALOG_ID = 'messages-reducer/SET_DIALOG_ID';
const SET_NEW_DIALOG = 'messages-reducer/SET_NEW_DIALOG';

let initialState = {
    dialogItemsData: [
        {
            name: 'Suarez', userId: 5081, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/5081/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1, date: 1574411457616 },
                { message: 'Great! How are you?', id: 2, right: true, date: 1574411457616 },
                { message: 'Im fine! Go work!', id: 3, date: 1574411457616 },
                { message: 'Lets go!!!', id: 4, right: true, date: 1574411457616 },
            ]

        },
        {
            name: 'Siarhei', userId: 5217, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/5217/user-small.jpg?v=13',
            messages: [
                { message: 'Hey,how are you?', id: 1, date: 1574411457616 },
                { message: 'Great! How are you?', id: 2, right: true, date: 1574411457616 }
            ]
        },
        {
            name: 'SeregaLucky', userId: 1670, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/1670/user-small.jpg?v=13',
            messages: [
                { message: 'Hello!', id: 1, date: 1574411457616 },
                {
                    message: 'A group of people born on the same day, including Rebecca (Moore) and Jack (Ventimiglia), a married couple expecting triplets in Pittsburgh, and Kevin (Hartley), a handsome television actor growing bored of his fly bachelor lifestyle.',
                    id: 2, right: true, date: 1574411457616
                },
                { message: 'WOW!', id: 3, date: 1574411457616 },
                { message: 'YEAH, I LIKE IT!!!', id: 4, right: true, date: 1574411457616 },
            ]
        },
        {
            name: 'Andrej', userId: 4987, avatar: 'https://social-network.samuraijs.com/activecontent/images/users/4987/user-small.jpg?v=13',
            messages: [
                { message: 'DO YOU KNOW REACT?', id: 1, date: 1574411457616 },
                { message: 'yes, what about u?', id: 2, right: true, date: 1574411457616 },
                { message: 'Me too!', id: 3 },
                { message: 'React better than vue', id: 4, right: true, date: 1574411457616 },
            ]
        }
    ],
    currentDialogId: 0
}

const messagesReducer = (state = initialState, action) => {

    switch (action.type) {
        case SEND_MESSAGE:
            debugger;
            return {
                ...state,
                dialogItemsData: state.dialogItemsData.map((i) => {
                    return i.userId === action.id
                        ? { ...i, messages: [...i.messages, { message: action.message, id: i.messages.length + 1, right: true, date: action.date }] }
                        : { ...i }
                }).sort((a, b) => { return b.messages[b.messages.length - 1].date - a.messages[a.messages.length - 1].date })
            }
        case SET_DIALOG_ID:
            return {
                ...state,
                currentDialogId: action.id
            }
        case SET_NEW_DIALOG:
            return {
                ...state,
                dialogItemsData: [...state.dialogItemsData, {
                    name: action.name, userId: action.userId, avatar: action.photo || avatar,
                    messages: [{ message: action.message, id: 1, right: true, date: action.date }]
                }],
                currentDialogId: action.userId
            }

        default: return state;
    }

}

export const sendMessage = (message, id) => ({ type: SEND_MESSAGE, message, id, date: Date.now() });
export const setDialogID = (id) => ({ type: SET_DIALOG_ID, id });
export const newDialog = (message, userId, photo, name) => ({ type: SET_NEW_DIALOG, userId, photo, name, message, date: Date.now() })
export const messageFromPopUp = (data, user) => (dispatch, getState) => {

    const userId = user.id || user.userId;
    const photo = user.photos.small;
    const name = user.name || user.fullName;

    if (getState().Messages.dialogItemsData.find(i => i.userId === userId)) {

        dispatch(sendMessage(data.messagePopup, userId))
    }
    else {
        dispatch(newDialog(data.messagePopup, userId, photo, name))
    }
}
export const search = (word) => ({type: SEARCH_DIALOG, word})
export default messagesReducer;
