import React, {useEffect, useRef, useState} from 'react'

import styles from './Messages.module.css'
import DialogWithUser from '../DialogsList/DialogWithUser'
import Dialog from './Dialog'









const Messages = ({dialogItemsData, currentDialogId, ...props}) => {
    const [searchWord, setSearchWord] = useState(null);
    const handleSearch = (e)=>{
        setSearchWord(e.currentTarget.value);
        if(e.currentTarget.value && e.currentTarget.value.trim() !== ''){
            
        }   
    }

    let dialogItems = dialogItemsData.map((item, index) => <DialogWithUser currentDialogId={currentDialogId} avatar={item.avatar}
    lastMessage={ item.messages[item.messages.length-1].message} setDialogID={props.setDialogID}   key={index+1} name={item.name} id={item.userId} />)
    const dialog = useRef();
    const currentDialog = dialogItemsData[dialogItemsData.findIndex((i)=>{return i.userId === currentDialogId})];
    useEffect(()=>{
       
    },[dialog])

    return <div className={styles.messages}>
        {currentDialogId === 0
            ? <div className={styles.emptyMessages}>
                <div className={styles.iconMessage}></div>
                <div className={styles.selectDialog}>
                    Please select a dialog
                </div>
             </div>
            : <Dialog ref={dialog} userId={currentDialog.userId} sendMessage={props.sendMessage} name={currentDialog.name} 
            avatar={currentDialog.avatar}  messages={currentDialog.messages} />}

        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}><input value={searchWord} onChange={e=>handleSearch(e)} type="text" name="" id=""/>{}</div>
            <div className={styles.dialogsList}>
                {dialogItems}

            </div>
        </div>
        
    </div>
}


export default Messages;
