import React from 'react';
import Preloader from '../../common/Preloader/Preloader';
import styles from './profileInfo.module.css';
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import avatar from '../../../assets/img/ava.jpg';

const ProfileInfo = (props) => {
    const updatePhoto = (e) => {

        if (e.target.files[0]) {
            props.savePhoto(e.target.files[0])
        }


    }
    debugger;
    return props.profile
        
        ? <div className={styles.profileInfoContainer}>
            <div className={styles.profileInfo}>
                <div className={props.isMyPage ? styles.avatarContainer + ' ' + styles.avatarUpdate : styles.avatarContainer}>
                    <img className={styles.avatar} src={props.profile.photos.large ? props.profile.photos.large : avatar} alt="" />
                    {props.isMyPage && 
                    <div className={styles.changePhoto}>
                        <label for="upload-photo" className={styles.changePhotoLabel}>Update photo</label>
                        <input className={styles.changePhotoInput} id="upload-photo" type='file' onChange={updatePhoto} /> 
                    </div>}
                </div>
                <div>
                    <h2>{props.profile.fullName}</h2>
                    <ProfileStatusWithHooks isMyPage={props.isMyPage} updateStatus={props.updateStatus} status={props.status} />
                    <span>Looking for a job: {props.profile.lookingForAJobDescription ? 'yes' : 'no'}</span>
                </div>
            </div>
        </div>
        : <Preloader />
}

export default ProfileInfo;
