import React, { useState } from 'react'
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'
import {  change } from 'redux-form'


const EmojiPicker = ({formName, fieldName, Form, styles, dispatch}) => {
    const [emojiPickerMode, setEPMode] = useState(false);
    
    const addEmoji = (e) => {
        debugger;
        const sym = e.unified.split('-')
        const codesArray = []

        sym.map(el => codesArray.push('0x' + el))
        const emoji = String.fromCodePoint(...codesArray)

        dispatch(change(formName, fieldName, Form.values ? Form.values[fieldName] + emoji : emoji));
    }

    return <div onMouseEnter={() => { setEPMode(true) }} onMouseLeave={() => { setEPMode(false) }}>
        <span role='button' className={styles.buttomEmoji}></span>
        {emojiPickerMode && <Picker style={{ position: 'absolute', bottom: '80px', right: '20px' }} onSelect={addEmoji} native={true} />}
    </div>

}

export default EmojiPicker;
