import React, {useEffect, useRef, useState} from 'react'

import styles from './Messages.module.css'
import DialogWithUser from '../DialogsList/DialogWithUser'
import Dialog from './Dialog'









const Messages = ({dialogItemsData, currentDialogId, search, dialogsFromSearch,...props}) => {
    const [searchWord, setSearchWord] = useState(null);
    const [isSearchMode, setSearchMode] = useState(false);
    

    const handleSearch = (e)=>{
        const word = e.currentTarget.value;
        setSearchWord(word);
        if(word && word.trim() !== ''){
            search(word);
            setSearchMode(true);
            document.addEventListener('click', outSearh)
        }   
    }
    const outSearh = (e) =>{
       
        setTimeout(() => {
            setSearchMode(false);
        setSearchWord('');
        }, 100);
        
        
        
    }

    
    const dialogItems = dialogItemsData.map((item, index) => <DialogWithUser currentDialogId={currentDialogId} avatar={item.avatar} 
        date={ item.messages[item.messages.length-1].date} lastMessage={ item.messages[item.messages.length-1].message} setDialogID={props.setDialogID}   
        key={index+1} name={item.name} id={item.userId} />)

    const dialogItemsSearch = isSearchMode && dialogsFromSearch.map((item, index) => <DialogWithUser isSearchMode={isSearchMode} 
        currentDialogId={currentDialogId} avatar={item.avatar} outSearh={outSearh} lastMessage={ item.messages[item.messages.length-1].message}
        date={ item.messages[item.messages.length-1].date} setDialogID={props.setDialogID}   key={index+1} name={item.name} id={item.userId} />)
    
    const currentDialog = dialogItemsData[dialogItemsData.findIndex((i)=>{return i.userId === currentDialogId})];
   

    return <div className={styles.messages} >
        
        {currentDialogId === 0
            ? <div className={styles.emptyMessages}>
                <div className={styles.iconMessage}></div>
                <div className={styles.selectDialog}>
                    Please select a dialog
                </div>
             </div>
            : <Dialog valueMessage ={props.valueMessage} isSearchMode={isSearchMode}  userId={currentDialog.userId} sendMessage={props.sendMessage} name={currentDialog.name} 
            avatar={currentDialog.avatar}  messages={currentDialog.messages} />}

        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}><input placeholder='Search' className={styles.searchInput} onBlur={outSearh} value={searchWord}  onChange={e=>handleSearch(e)} type="text" name="" id=""/></div>
            <div className={styles.dialogsList}>
                {isSearchMode ? dialogItemsSearch : dialogItems }
        
            </div>
        </div>
        
    </div>
}


export default Messages;
