import React from 'react';
import './App.css'
import SideBar from './components/SideBar/SideBar'
import { withRouter, HashRouter, Route } from 'react-router-dom'
import ProfileContainer from './components/Profile/ProfileContainer'
import News from './components/News/News'
import Communities from './components/Communities/Communities'
import Events from './components/Events/Events'
import Pages from './components/Pages/Pages'
import Manage from './components/Manage/Manage'
import UsersContainer from './components/Friends/UsersContainer'
import FooterContainer from './components/Footer/FooterContainer';
import Login from './components/Login/Login'
import { connect, Provider } from 'react-redux';
import { initializeAppThunk } from './redux/app-reducer';
import Preloader from './components/common/Preloader/Preloader';
import { compose } from 'redux';
import store from './redux/redux-store'
import withSuspense from './hoc/withSuspense'

const MessagesContainer = React.lazy(() => import('./components/Messages-container/MessagesContainer'))
// function requireAll(r) { r.keys().forEach(r); } //import files
// requireAll(require.context('./img/', true));


class App extends React.Component {

  componentDidMount() {
    this.props.initializeAppThunk()
  }
  render() {
    if (!this.props.initialized) return <Preloader />

    return (
      <>
        <div className='app-wrapper'>
          <SideBar />
          <div className='app-wrapper-content'>
            <switch>
              <Route exact path='/' path='/profile/:userId?' render={() => <ProfileContainer />} />
              <Route path='/profile/:userId?' render={() => <ProfileContainer />} />
              <Route path='/news' component={News} />
              <Route exact path='/messages' render={withSuspense(MessagesContainer)} />
              <Route path='/friends' render={() => <UsersContainer />} />
              <Route path='/communities' component={Communities} />
              <Route path='/events' component={Events} />
              <Route path='/pages' component={Pages} />
              <Route path='/manage' component={Manage} />
              <Route path='/login' component={Login} />
            </switch>
          </div>
        </div>
        <FooterContainer />
      </>
    );
  }
}

const mapStateToPropse = (state) => (
  {
    initialized: state.app.initialized
  }
)

const AppContainer = compose(
  withRouter,
  connect(mapStateToPropse, { initializeAppThunk }))(App)

const HightApp = () => {
  return <HashRouter>
    <Provider store={store}>
      <AppContainer />
    </Provider>
  </HashRouter>
}
export default HightApp;
