import React from 'react';
import styles from './DialogWithUser.module.css'


const DialogWithUser = (props) => {
    
const options = {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    timezone: 'UTC',
    hour: 'numeric',
    minute: 'numeric'
}

    return <div onClick={() => {
        props.setDialogID(props.id)
        props.isSearchMode && props.outSearh()
    }} className={styles.items + ' ' + (props.currentDialogId === props.id ? styles.selectedDialog : '')}>
        <div>
            <img className={styles.avatar} src={props.avatar} alt="avatar" />
        </div>
        <div>
            <div className={styles.name}>
                {props.name}
            </div>
            <span className={styles.message}>{props.lastMessage.length > 15 ? props.lastMessage.slice(0, 15) + '...' : props.lastMessage}</span>
        </div>
    </div>
}

export default DialogWithUser;
