import React from 'react';

import styles from './profileInfo.module.css';


class ProfileStatus extends React.Component {

    state = {
        editMode: false
    }
    editStatus() {
        this.setState({
            editMode: true
        })
    }
    outEditStatus() {
        this.setState({
            editMode: false
        })
    }

    render() {
        return <div>
            {this.state.editMode
                ? <div>
                    <span onDoubleClick={this.editStatus.bind(this)}>{this.props.status}</span>
                </div>
                : <div>
                    <input onDoubleClick={this.outEditStatus.bind(this)}></input>
                </div>

            }

        </div>
    }
}

export default ProfileStatus;
