import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { logInThunk } from '../../redux/auth-reducer';
import { connect } from 'react-redux';
import { Input, createField } from '../common/FormControls/FormControls';
import { required } from '../../utils/validators/validator';
import { Redirect } from 'react-router';
import styles from '../common/FormControls/FormControls.module.css'

const LoginForm = ({error, handleSubmit}) => {
    return (
        <form onSubmit={handleSubmit}>
            {createField('Login', 'login',  [required], Input)}
            {createField('Password', 'password',  [required], Input)}
            <div>
                <Field placeholder={'Password'} name='password' component={Input} validate={[required]} />
            </div>  
            <div>
                <Field type={'checkbox'} id='rememberMe' name='rememberMe' component={Input} />
                <label htmlFor='rememberMe'> remember me </label>
            </div>
            {error && <div className={styles.errorLogIn}>{error}</div>}
            <button type='submit'>Login</button>
        </form>
    )
}

const LoginReduxForm = reduxForm({ form: 'login' })(LoginForm);

const Login = ({logInThunk, isAuth}) => {
    const onSubmit = (data) => {
        console.log(data);
        logInThunk(data);
    }
    if (isAuth) {
        return <Redirect to={'/profile'} />
    }
    
    return <div>
        <h1>LOGIN </h1>
        <LoginReduxForm onSubmit={onSubmit} />
    </div >
}
const mapDispatchToProps = (state)=> ({
    isAuth: state.auth.isAuth
})

export default connect(mapDispatchToProps, { logInThunk })(Login);
