import React, {useEffect, useRef} from 'react'

import styles from './Messages.module.css'
import DialogItem from '../../DialogItem/DialogItem'
import MessageItem from '../../MessageItem/MessageItem'

import { reduxForm, Field } from 'redux-form';
import { createField } from '../../common/FormControls/FormControls';
import { required, maxLengthCreator } from '../../../utils/validators/validator';
import CustomScroll from 'react-custom-scroll';
import Scrollbar from "react-scrollbars-custom";

const maxLength30 = maxLengthCreator(30);


const Messages = (props) => {
    let messageItems = props.Messages.messagesData.map(item => <MessageItem right={item.right} message={item.message} id={item.id} />)
    let dialogItems = props.Messages.dialogItemsData.map((item) => <DialogItem name={item.name} id={item.id} />)

   const messages = useRef();
   let heightMessages = messages.getBoundingClientRect().height;
   
   useEffect(()=>{
    heightMessages = messages.current.getBoundingClientRect().height;
    console.log(heightMessages);
   },[messageItems])
    

    

    return <div className={styles.messages}>
        <div className={styles.dialog}>
            <div className={styles.dialogHead}></div>
        
            {/* <CustomScroll  keepAtBottom={true}  >
                <div  className={styles.messagesFill}>
                    {messageItems}
                    
            </CustomScroll>
              */}
            <Scrollbar  scrollTop={ heightMessages  } removeTracksWhenNotUsed={true} style={{ width: "100%", height: "70%" }}>
                <div ref={messages}   className={styles.messagesFill}>
                    {messageItems}
                </div>  
            </Scrollbar>
            <AddMessageFormRedux onSubmit={props.sendMessage}/> 
        </div>
        <div className={styles.dialogItems}>
            <div className={styles.dialogSearch}></div>
            <div className={styles.dialogsList}>
                {dialogItems}
                
            </div>
        </div>
    </div>
}

const addMessageForm = (props) => {
    return <form onSubmit={props.handleSubmit}>
        {createField(null, 'message', [required], 'textarea', null, null,{cols: 100, rows: 4})}
        <div>
            <button className={styles.buttonForm} type='submit'>Send</button>
        </div>
    </form>
}
const AddMessageFormRedux = reduxForm({form: 'addMessageForm'})(addMessageForm);

export default Messages;
